import{_ as i,c as e,o as l,a as o}from"./app.4fe42109.js";const m=JSON.parse('{"title":"Magisk Êõ¥Êñ∞Êó•Âøó","description":"","frontmatter":{},"headers":[{"level":2,"title":"v25.2","slug":"v25-2","link":"#v25-2","children":[]},{"level":2,"title":"v25.1","slug":"v25-1","link":"#v25-1","children":[]},{"level":2,"title":"v25.0","slug":"v25-0","link":"#v25-0","children":[]},{"level":2,"title":"v24.3","slug":"v24-3","link":"#v24-3","children":[]},{"level":2,"title":"v24.2","slug":"v24-2","link":"#v24-2","children":[]},{"level":2,"title":"v24.1","slug":"v24-1","link":"#v24-1","children":[]},{"level":2,"title":"v24.0","slug":"v24-0","link":"#v24-0","children":[]},{"level":2,"title":"v23.0","slug":"v23-0","link":"#v23-0","children":[]},{"level":2,"title":"v22.1","slug":"v22-1","link":"#v22-1","children":[]},{"level":2,"title":"v22.0","slug":"v22-0","link":"#v22-0","children":[]},{"level":2,"title":"v21.4","slug":"v21-4","link":"#v21-4","children":[]},{"level":2,"title":"v21.3","slug":"v21-3","link":"#v21-3","children":[]},{"level":2,"title":"v21.2","slug":"v21-2","link":"#v21-2","children":[]},{"level":2,"title":"v21.1","slug":"v21-1","link":"#v21-1","children":[]},{"level":2,"title":"v21.0","slug":"v21-0","link":"#v21-0","children":[]},{"level":2,"title":"v20.4","slug":"v20-4","link":"#v20-4","children":[]},{"level":2,"title":"v20.3","slug":"v20-3","link":"#v20-3","children":[]},{"level":2,"title":"v20.2","slug":"v20-2","link":"#v20-2","children":[]},{"level":2,"title":"v20.1","slug":"v20-1","link":"#v20-1","children":[]},{"level":2,"title":"v20.0","slug":"v20-0","link":"#v20-0","children":[]},{"level":2,"title":"v19.4","slug":"v19-4","link":"#v19-4","children":[]},{"level":2,"title":"v19.3","slug":"v19-3","link":"#v19-3","children":[]},{"level":2,"title":"v19.2","slug":"v19-2","link":"#v19-2","children":[]},{"level":2,"title":"v19.1","slug":"v19-1","link":"#v19-1","children":[]},{"level":2,"title":"v19.0","slug":"v19-0","link":"#v19-0","children":[]},{"level":2,"title":"v18.1","slug":"v18-1","link":"#v18-1","children":[]},{"level":2,"title":"v18.0","slug":"v18-0","link":"#v18-0","children":[]},{"level":2,"title":"v17.3","slug":"v17-3","link":"#v17-3","children":[]},{"level":2,"title":"v17.2","slug":"v17-2","link":"#v17-2","children":[]},{"level":2,"title":"v17.0/17.1","slug":"v17-0-17-1","link":"#v17-0-17-1","children":[]},{"level":2,"title":"v16.7","slug":"v16-7","link":"#v16-7","children":[]},{"level":2,"title":"v16.6","slug":"v16-6","link":"#v16-6","children":[]},{"level":2,"title":"v16.4","slug":"v16-4","link":"#v16-4","children":[]},{"level":2,"title":"v16.3","slug":"v16-3","link":"#v16-3","children":[]},{"level":2,"title":"v16.2","slug":"v16-2","link":"#v16-2","children":[]},{"level":2,"title":"v16.1","slug":"v16-1","link":"#v16-1","children":[]},{"level":2,"title":"v16.0","slug":"v16-0","link":"#v16-0","children":[]},{"level":2,"title":"v15.4","slug":"v15-4","link":"#v15-4","children":[]},{"level":2,"title":"v15.3","slug":"v15-3","link":"#v15-3","children":[]},{"level":2,"title":"v15.2","slug":"v15-2","link":"#v15-2","children":[]},{"level":2,"title":"v15.1","slug":"v15-1","link":"#v15-1","children":[]},{"level":2,"title":"v15.0","slug":"v15-0","link":"#v15-0","children":[]},{"level":2,"title":"v14.6 (1468)","slug":"v14-6-1468","link":"#v14-6-1468","children":[]},{"level":2,"title":"v14.5 (1456)","slug":"v14-5-1456","link":"#v14-5-1456","children":[]},{"level":2,"title":"v14.5 (1455)","slug":"v14-5-1455","link":"#v14-5-1455","children":[]},{"level":2,"title":"v14.3 (1437)","slug":"v14-3-1437","link":"#v14-3-1437","children":[]},{"level":2,"title":"v14.2","slug":"v14-2","link":"#v14-2","children":[]},{"level":2,"title":"v14.1","slug":"v14-1","link":"#v14-1","children":[]},{"level":2,"title":"v14.0","slug":"v14-0","link":"#v14-0","children":[]},{"level":2,"title":"v13.3","slug":"v13-3","link":"#v13-3","children":[]},{"level":2,"title":"v13.2","slug":"v13-2","link":"#v13-2","children":[]},{"level":2,"title":"v13.1","slug":"v13-1","link":"#v13-1","children":[]},{"level":2,"title":"v12.0","slug":"v12-0","link":"#v12-0","children":[]},{"level":2,"title":"v11.5/11.6","slug":"v11-5-11-6","link":"#v11-5-11-6","children":[]},{"level":2,"title":"v11.1","slug":"v11-1","link":"#v11-1","children":[]},{"level":2,"title":"v11.0","slug":"v11-0","link":"#v11-0","children":[]},{"level":2,"title":"v10.2","slug":"v10-2","link":"#v10-2","children":[]},{"level":2,"title":"v10","slug":"v10","link":"#v10","children":[]},{"level":2,"title":"v9","slug":"v9","link":"#v9","children":[]},{"level":2,"title":"v8","slug":"v8","link":"#v8","children":[]},{"level":2,"title":"v7","slug":"v7","link":"#v7","children":[]},{"level":2,"title":"v6","slug":"v6","link":"#v6","children":[]},{"level":2,"title":"v5","slug":"v5","link":"#v5","children":[]},{"level":2,"title":"v4","slug":"v4","link":"#v4","children":[]},{"level":2,"title":"v3","slug":"v3","link":"#v3","children":[]},{"level":2,"title":"v2","slug":"v2","link":"#v2","children":[]},{"level":2,"title":"v1","slug":"v1","link":"#v1","children":[]},{"level":2,"title":"ÂèÇËÄÉÈìæÊé•","slug":"ÂèÇËÄÉÈìæÊé•","link":"#ÂèÇËÄÉÈìæÊé•","children":[]}],"relativePath":"changes.md","lastUpdated":1674222670000}'),a={name:"changes.md"},t=o('<h1 id="magisk-Êõ¥Êñ∞Êó•Âøó" tabindex="-1">Magisk Êõ¥Êñ∞Êó•Âøó <a class="header-anchor" href="#magisk-Êõ¥Êñ∞Êó•Âøó" aria-hidden="true">#</a></h1><h2 id="v25-2" tabindex="-1">v25.2 <a class="header-anchor" href="#v25-2" aria-hidden="true">#</a></h2><ul><li>[MagiskInit] Fix a potential issue when stub cpio is used</li><li>[MagiskInit] Fix reboot to recovery when stub cpio is used</li><li>[MagiskInit] Fix sepolicy.rules symlink for rootfs devices</li><li>[General] Better data encryption detection</li><li>[General] Move the whole logging infrastructure into Rust</li></ul><h2 id="v25-1" tabindex="-1">v25.1 <a class="header-anchor" href="#v25-1" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Fix ramdisk backup being incorrectly skipped</li><li>[MagiskBoot] Add new feature to detect unsupported dtb and abort during installation</li><li>[Zygisk] Change binary hijack paths</li><li>[App] Fix incorrect recovery mode detection and installation</li><li>[MagiskInit] Fix config not properly exported in legacy SAR devices</li><li>[General] Enforce the Magisk app to always match or be newer than <code>magiskd</code></li></ul><h2 id="v25-0" tabindex="-1">v25.0 <a class="header-anchor" href="#v25-0" aria-hidden="true">#</a></h2><ul><li>[MagiskInit] Update 2SI implementation, significantly increase device compatibility (e.g. Sony Xperia devices)</li><li>[MagiskInit] Introduce new <code>sepolicy</code> injection mechanism</li><li>[MagiskInit] Support Oculus Go</li><li>[MagiskInit] Support Android 13 GKIs (Pixel 6)</li><li>[MagiskBoot] Fix vbmeta extraction implementation</li><li>[App] Fix stub app on older Android versions</li><li>[App] [MagiskSU] Properly support apps using <code>sharedUserId</code></li><li>[MagiskSU] Fix a possible crash in <code>magiskd</code></li><li>[MagiskSU] Prune unused UIDs as soon as <code>system_server</code> restarts to prevent UID reuse attacks</li><li>[MagiskSU] Verify and enforce the installed Magisk app&#39;s certificate to match the distributor&#39;s signature</li><li>[MagiskSU] [Zygisk] Proper package management and detection</li><li>[Zygisk] Fix function hooking on devices running Android 12 with old kernels</li><li>[Zygisk] Fix Zygisk&#39;s self code unloading implementation</li><li>[DenyList] Fix DenyList on shared UID apps</li><li>[BusyBox] Add workaround for devices running old kernels</li></ul><h2 id="v24-3" tabindex="-1">v24.3 <a class="header-anchor" href="#v24-3" aria-hidden="true">#</a></h2><ul><li>[General] Stop using <code>getrandom</code> syscall</li><li>[Zygisk] Update API to v3, adding new fields to <code>AppSpecializeArgs</code></li><li>[App] Improve app repackaging installation workflow</li></ul><h2 id="v24-2" tabindex="-1">v24.2 <a class="header-anchor" href="#v24-2" aria-hidden="true">#</a></h2><ul><li>[MagiskSU] Fix buffer overflow</li><li>[MagiskSU] Fix owner managed multiuser superuser settings</li><li>[MagiskSU] Fix command logging when using <code>su -c &lt;cmd&gt;</code></li><li>[MagiskSU] Prevent su request indefinite blocking</li><li>[MagiskBoot] Support <code>lz4_legacy</code> archive with multiple magic</li><li>[MagiskBoot] Fix <code>lz4_lg</code> compression</li><li>[DenyList] Allow targeting processes running as system UID</li><li>[Zygisk] Workaround Samsung&#39;s &quot;early zygote&quot;</li><li>[Zygisk] Improved Zygisk loading mechanism</li><li>[Zygisk] Fix application UID tracking</li><li>[Zygisk] Fix improper <code>umask</code> being set in zygote</li><li>[App] Fix BusyBox execution test</li><li>[App] Improve stub loading mechanism</li><li>[App] Major app upgrade flow improvements</li><li>[General] Improve commandline error handling and messaging</li></ul><h2 id="v24-1" tabindex="-1">v24.1 <a class="header-anchor" href="#v24-1" aria-hidden="true">#</a></h2><ul><li>[App] Stability improvements</li></ul><h2 id="v24-0" tabindex="-1">v24.0 <a class="header-anchor" href="#v24-0" aria-hidden="true">#</a></h2><ul><li>[General] MagiskHide is removed from Magisk</li><li>[General] Support Android 12</li><li>[General] Support devices that do not support 32-bit and only runs 64-bit code</li><li>[General] Update BusyBox to 1.34.1</li><li>[Zygisk] Introduce new feature: Zygisk</li><li>[Zygisk] Introduce DenyList feature to revert Magisk features in user selected processes</li><li>[MagiskBoot] Support patching 32-bit kernel zImages</li><li>[MagiskBoot] Support boot image header v4</li><li>[MagiskBoot] Support patching out <code>skip_initramfs</code> from dtb bootargs</li><li>[MagiskBoot] Add new env variable <code>PATCHVBMETAFLAG</code> to configure whether vbmeta flags should be patched</li><li>[MagiskInit] Support loading fstab from <code>/system/etc</code> (required for Pixel 6)</li><li>[MagiskInit] Support <code>/proc/bootconfig</code> for loading boot configurations</li><li>[MagiskInit] Better support for some Meizu devices</li><li>[MagiskInit] Better support for some OnePlus/Oppo/Realme devices</li><li>[MagiskInit] Support <code>init.real</code> on some Sony devices</li><li>[MagiskInit] Skip loading Magisk when detecting DSU</li><li>[MagiskPolicy] Load <code>*_compat_cil_file</code> from system_ext</li><li>[MagiskSU] Use isolated devpts if the kernel supports it</li><li>[MagiskSU] Fix root shell if isolated mount namespace is set</li><li>[resetprop] Deleted properties are now wiped from memory instead of just unlinking</li><li>[App] Build a single APK for all ABIs</li><li>[App] Switch to use standard bottom navigation bar</li><li>[App] Downloading modules from the centralized Magisk-Modules-Repo is removed</li><li>[App] Support user configuration of boot image vbmeta patching</li><li>[App] Restore the ability to install Magisk on the other slot on some A/B devices</li><li>[App] Allow modules to specify an update URL for in-app update + install</li></ul><h2 id="v23-0" tabindex="-1">v23.0 <a class="header-anchor" href="#v23-0" aria-hidden="true">#</a></h2><ul><li>[App] Update snet extension. This fixes SafetyNet API errors.</li><li>[App] Fix a bug in the stub app that causes APK installation to fail</li><li>[App] Hide annoying errors in logs when hidden as stub</li><li>[App] Fix issues when patching ODIN tar files when the app is hidden</li><li>[General] Remove all pre Android 5.0 support</li><li>[General] Update BusyBox to use proper libc</li><li>[General] Fix C++ undefined behaviors</li><li>[General] Several <code>sepolicy.rule</code> copy/installation fixes</li><li>[MagiskPolicy] Remove unnecessary sepolicy rules</li><li>[MagiskHide] Update package and process name validation logic</li><li>[MagiskHide] Some changes that prevents zygote deadlock</li></ul><h2 id="v22-1" tabindex="-1">v22.1 <a class="header-anchor" href="#v22-1" aria-hidden="true">#</a></h2><ul><li>[App] Prevent multiple installation sessions running in parallel</li><li>[App] Prevent OutOfMemory crashes when checking boot signature on PXA boot images</li><li>[General] Proper cgroup migration implementation</li><li>[General] Rewrite log writer from scratch, should resolve any crashes and deadlocks</li><li>[General] Many scripts updates fixing regressions</li><li>[MagiskHide] Prevent possible deadlock when signal arrives</li><li>[MagiskHide] Partial match process names if necessary</li><li>[MagiskBoot] Preserve and patch AVB 2.0 structures/headers in boot images</li><li>[MagiskBoot] Properly strip out data encryption flags</li><li>[MagiskBoot] Prevent possible integer overflow</li><li>[MagiskInit] Fix <code>sepolicy.rule</code> mounting strategy</li><li>[resetprop] Always delete existing <code>ro.</code> props before updating. This will fix bootloops that could be caused by modifying device fingerprint properties.</li></ul><h2 id="v22-0" tabindex="-1">v22.0 <a class="header-anchor" href="#v22-0" aria-hidden="true">#</a></h2><ul><li>[General] Magisk and Magisk Manager is now merged into the same package!</li><li>[App] The term &quot;Magisk Manager&quot; is no longer used elsewhere. We refer it as the Magisk app.</li><li>[App] Support hiding the Magisk app with advanced technique (stub APK loading) on Android 5.0+ (it used to be 9.0+)</li><li>[App] Disallow re-packaging the Magisk app on devices lower than Android 5.0</li><li>[App] Detect and warn about multiple invalid states and provide instructions on how to resolve it</li><li>[MagiskHide] Fix a bug when stopping MagiskHide does not take effect</li><li>[MagiskBoot] Fix bug when unpacking <code>lz4_lg</code> compressed boot images</li><li>[MagiskInit] Support Galaxy S21 series</li><li>[MagiskSU] Fix incorrect APEX paths that caused <code>libsqlite.so</code> fail to load</li></ul><h2 id="v21-4" tabindex="-1">v21.4 <a class="header-anchor" href="#v21-4" aria-hidden="true">#</a></h2><ul><li>[MagiskSU] Fix <code>su -c</code> behavior that broke many root apps</li><li>[General] Properly handle read/write over sockets (the <code>broken pipe</code> issue)</li></ul><h2 id="v21-3" tabindex="-1">v21.3 <a class="header-anchor" href="#v21-3" aria-hidden="true">#</a></h2><ul><li>[MagiskInit] Avoid mounting <code>f2fs</code> userdata as it may result in kernel crashes. This shall fix a lot of bootloops</li><li>[MagiskBoot] Fix a minor header checksum bug for <code>DHTB</code> header and ASUS <code>blob</code> image formats</li><li>[MagiskHide] Allowing hiding isolated processes if the mount namespace is separated</li></ul><h2 id="v21-2" tabindex="-1">v21.2 <a class="header-anchor" href="#v21-2" aria-hidden="true">#</a></h2><ul><li>[MagiskInit] Detect 2SI after mounting <code>system_root</code> on legacy SAR devices</li><li>[General] Make sure <code>post-fs-data</code> scripts cannot block more than 35 seconds</li><li>[General] Fix the <code>magisk --install-module</code> command</li><li>[General] Trim Windows newline when reading files</li><li>[General] Directly log to file to prevent <code>logcat</code> weirdness</li><li>[MagiskBoot] Fix header dump/load for header v3 images</li></ul><h2 id="v21-1" tabindex="-1">v21.1 <a class="header-anchor" href="#v21-1" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Support boot header v3 (Pixel 5 and 4a 5G)</li><li>[MagiskBoot] Distinguish <code>lz4_lg</code> and <code>lz4_legacy</code> (Pixel 5 and 4a 5G)</li><li>[MagiskBoot] Support vendor boot images (for dev, not relevant for Magisk installation)</li><li>[MagiskInit] Support kernel cmdline <code>androidboot.fstab_suffix</code></li><li>[MagiskInit] Support kernel initialized dm-verity on legacy SAR</li><li>[General] Significantly broaden sepolicy.rule compatibility</li><li>[General] Add Magisk binaries to <code>PATH</code> when executing boot scripts</li><li>[General] Update <code>--remove-modules</code> command implementation</li><li>[General] Make Magisk properly survive after factory reset on Android 11</li><li>[MagiskSU] Add APEX package <code>com.android.i18n</code> to <code>LD_LIBRARY_PATH</code> when linking <code>libsqlite.so</code></li><li>[MagiskHide] Support hiding apps installed in secondary users (e.g. work profile)</li><li>[MagiskHide] Make zygote detection more robust</li></ul><h2 id="v21-0" tabindex="-1">v21.0 <a class="header-anchor" href="#v21-0" aria-hidden="true">#</a></h2><ul><li>[General] Support Android 11 üéâ</li><li>[General] Add Safe Mode detection. Disable all modules when the device is booting into Safe Mode.</li><li>[General] Increase <code>post-fs-data</code> mode timeout from 10 seconds to 40 seconds</li><li>[MagiskInit] Rewritten 2SI support from scratch</li><li>[MagiskInit] Support when no <code>/sbin</code> folder exists (Android 11)</li><li>[MagiskInit] Dump fstab from device-tree to rootfs and force <code>init</code> to use it for 2SI devices</li><li>[MagiskInit] Strip out AVB for 2SI as it may cause bootloop</li><li>[Modules] Rewritten module mounting logic from scratch</li><li>[MagiskSU] For Android 8.0+, a completely new policy setup is used. This reduces compromises in Android&#39;s sandbox, providing more policy isolation and better security for root users.</li><li>[MagiskSU] Isolated mount namespace will now first inherit from parent process, then isolate itself from the world</li><li>[MagiskSU] Update communication protocol with Magisk Manager to work with the hardened SELinux setup</li><li>[MagiskPolicy] Optimize match all rules. This will significantly reduce policy binary size and save memory and improve general kernel performance.</li><li>[MagiskPolicy] Support declaring new types and attributes</li><li>[MagiskPolicy] Make policy statement closer to stock <code>*.te</code> format. Please check updated documentation or <code>magiskpolicy --help</code> for more details.</li><li>[MagiskBoot] Support compressed <code>extra</code> blobs</li><li>[MagiskBoot] Pad boot images to original size with zeros</li><li>[MagiskHide] Manipulate additional vendor properties</li></ul><h2 id="v20-4" tabindex="-1">v20.4 <a class="header-anchor" href="#v20-4" aria-hidden="true">#</a></h2><ul><li>[MagiskInit] Fix potential bootloop in A-only 2SI devices</li><li>[MagiskInit] Properly support Tegra partition naming</li><li>[General] Load <a href="http://libsqlite.so" target="_blank" rel="noreferrer">libsqlite.so</a> dynamically, which removes the need to use wrapper scripts on Android 10+</li><li>[General] Detect API level with a fallback method on some devices</li><li>[General] Workaround possible bug in x86 kernel readlinkat system call</li><li>[BusyBox] Enable SELinux features. Add chcon/runcon etc., and &#39;-Z&#39; option to many applets</li><li>[BusyBox] Introduce standalone mode. More details in release notes</li><li>[MagiskHide] Disable MagiskHide by default</li><li>[MagiskHide] Add more potential detectable system properties</li><li>[MagiskHide] Add workaround for Xiaomi devices bootloop when MagiskHide is enabled on cross region ROMs</li><li>[MagiskBoot] Support patching special Motorolla DTB format</li><li>[MagiskPolicy] Support &#39;genfscon&#39; sepolicy rules</li><li>[Scripts] Support NAND based boot images (character nodes in /dev/block)</li><li>[Scripts] Better addon.d (both v1 and v2) support</li><li>[Scripts] Support Lineage Recovery for Android 10+</li></ul><h2 id="v20-3" tabindex="-1">v20.3 <a class="header-anchor" href="#v20-3" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Fix <code>lz4_legacy</code> decompression</li></ul><h2 id="v20-2" tabindex="-1">v20.2 <a class="header-anchor" href="#v20-2" aria-hidden="true">#</a></h2><ul><li>[MagiskSU] Properly handle communication between daemon and application (root request prompt)</li><li>[MagiskInit] Fix logging in kmsg</li><li>[MagiskBoot] Support patching dtb/dtbo partition formats</li><li>[General] Support pre-init sepolicy patch in modules</li><li>[Scripts] Update magisk stock image backup format</li></ul><h2 id="v20-1" tabindex="-1">v20.1 <a class="header-anchor" href="#v20-1" aria-hidden="true">#</a></h2><ul><li>[MagiskSU] Support component name agnostic communication (for stub APK)</li><li>[MagiskBoot] Set proper <code>header_size</code> in boot image headers (fix vbmeta error on Samsung devices)</li><li>[MagiskHide] Scan zygote multiple times</li><li>[MagiskInit] Support recovery images without /sbin/recovery binary. This will fix some A/B devices unable to boot to recovery after flashing Magisk</li><li>[General] Move acct to prevent daemon being killed</li><li>[General] Make sure &quot;--remove-modules&quot; will execute <a href="http://uninstall.sh" target="_blank" rel="noreferrer">uninstall.sh</a> after removal</li></ul><h2 id="v20-0" tabindex="-1">v20.0 <a class="header-anchor" href="#v20-0" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Support inject/modify <code>mnt_point</code> value in DTB fstab</li><li>[MagiskBoot] Support patching QCDT</li><li>[MagiskBoot] Support patching DTBH</li><li>[MagiskBoot] Support patching PXA-DT</li><li>[MagiskInit] [2SI] Support non A/B setup (Android 10)</li><li>[MagiskHide] Fix bug that reject process names with &quot;:&quot;</li><li>[MagicMount] Fix a bug that cause /product mirror not created</li></ul><h2 id="v19-4" tabindex="-1">v19.4 <a class="header-anchor" href="#v19-4" aria-hidden="true">#</a></h2><ul><li>[MagiskInit] [SAR] Boot system-as-root devices with system mounted as /</li><li>[MagiskInit] [2SI] Support 2-stage-init for A/B devices (Pixel 3 Android 10)</li><li>[MagiskInit] [initramfs] Delay sbin overlay creation to post-fs-data</li><li>[MagiskInit] [SARCompat] Old system-as-root implementation is deprecated, no more future changes</li><li>[MagiskInit] Add overlay.d support for root directory overlay for new system-as-root implementation</li><li>[MagiskSU] Unblock all signals in root shells (fix bash on Android)</li><li>[MagicMount] Support replacing files in /product</li><li>[MagiskHide] Support Android 10&#39;s Zygote blastula pool</li><li>[MagiskHide] All random strings now also have random length</li><li>[MagiskBoot] Allow no recompression for ramdisk.cpio</li><li>[MagiskBoot] Support some weird Huawei boot images</li><li>[General] Add new <code>--remove-modules</code> command to remove modules without root in ADB shell</li><li>[General] Support Android 10 new APEX libraries (Project Mainline)</li></ul><h2 id="v19-3" tabindex="-1">v19.3 <a class="header-anchor" href="#v19-3" aria-hidden="true">#</a></h2><ul><li>[MagiskHide] Hugely improve process monitor implementation, hopefully should no longer cause 100% CPU and daemon crashes</li><li>[MagiskInit] Wait for partitions to be ready for early mount, should fix bootloops on a handful of devices</li><li>[MagiskInit] Support EROFS used in EMUI 9.1</li><li>[MagiskSU] Properly implement mount namespace isolation</li><li>[MagiskBoot] Proper checksum calculation for header v2</li></ul><h2 id="v19-2" tabindex="-1">v19.2 <a class="header-anchor" href="#v19-2" aria-hidden="true">#</a></h2><ul><li>[General] Fix uninstaller</li><li>[General] Fix bootloops on some devices with tmpfs mounting to /data</li><li>[MagiskInit] Add Kirin hi6250 support</li><li>[MagiskSU] Stop claiming device focus for su logging/notify if feasible. This fix issues with users locking Magisk Manager with app lock, and prevent video apps get messed up when an app is requesting root in the background.</li></ul><h2 id="v19-1" tabindex="-1">v19.1 <a class="header-anchor" href="#v19-1" aria-hidden="true">#</a></h2><ul><li>[General] Support recovery based Magisk</li><li>[General] Support Android Q Beta 2</li><li>[MagiskInit] New sbin overlay setup process for better compatibility</li><li>[MagiskInit] Allow long pressing volume up to boot to recovery in recovery mode</li><li>[MagicMount] Use proper system_root mirror</li><li>[MagicMount] Use self created device nodes for mirrors</li><li>[MagicMount] Do not allow adding new files/folders in partition root folder (e.g. /system or /vendor)</li></ul><h2 id="v19-0" tabindex="-1">v19.0 <a class="header-anchor" href="#v19-0" aria-hidden="true">#</a></h2><ul><li>[General] Remove usage of magisk.img</li><li>[General] Add 64 bit magisk binary for native 64 bit support</li><li>[General] Support A only system-as-root devices that released with Android 9.0</li><li>[General] Support non EXT4 system and vendor partitions</li><li>[MagiskHide] Use Zygote ptracing for monitoring new processes</li><li>[MagiskHide] Targets are now per-application component</li><li>[MagiskInit] Support Android Q (no logical partition support yet!)</li><li>[MagiskPolicy] Support Android Q new split sepolicy setup</li><li>[MagiskInit] Move sbin overlay creation from main daemon post-fs-data to early-init</li><li>[General] Service scripts now run in parallel</li><li>[MagiskInit] Directly inject magisk services to init.rc</li><li>[General] Use lzma2 compressed ramdisk in extreme conditions</li><li>[MagicMount] Clone attributes from original file if exists</li><li>[MagiskSU] Use <code>ACTION_REBOOT</code> intent to workaround some OEM broadcast restrictions</li><li>[General] Use <code>skip_mount</code> instead of <code>auto_mount</code>: from opt-in to opt-out</li></ul><h2 id="v18-1" tabindex="-1">v18.1 <a class="header-anchor" href="#v18-1" aria-hidden="true">#</a></h2><ul><li>[General] Support EMUI 9.0</li><li>[General] Support Kirin 960 devices</li><li>[General] Support down to Android 4.2</li><li>[General] Major code base modernization under-the-hood</li></ul><h2 id="v18-0" tabindex="-1">v18.0 <a class="header-anchor" href="#v18-0" aria-hidden="true">#</a></h2><ul><li>[General] Migrate all code base to C++</li><li>[General] Modify database natively instead of going through Magisk Manager</li><li>[General] Deprecate path /sbin/.core, please start using /sbin/.magisk</li><li>[General] Boot scripts are moved from <code>&lt;magisk_img&gt;/.core/&lt;stage&gt;.d</code> to <code>/data/adb/&lt;stage&gt;.d</code></li><li>[General] Remove native systemless hosts (Magisk Manager is updated with a built-in systemless hosts module)</li><li>[General] Allow module <a href="http://post-fs-data.sh" target="_blank" rel="noreferrer">post-fs-data.sh</a> scripts to disable/remove modules</li><li>[MagiskHide] Use component names instead of process names as targets</li><li>[MagiskHide] Add procfs protection on SDK 24+ (Nougat)</li><li>[MagiskHide] Remove the folder /.backup to prevent detection</li><li>[MagiskHide] Hide list is now stored in database instead of raw textfile in images</li><li>[MagiskHide] Add &quot;--status&quot; option to CLI</li><li>[MagiskHide] Stop unmounting non-custom related mount points</li><li>[MagiskSU] Add <code>FLAG_INCLUDE_STOPPED_PACKAGES</code> in broadcasts to force wake Magisk Manager</li><li>[MagiskSU] Fix a bug causing SIGWINCH not properly detected</li><li>[MagiskPolicy] Support new av rules: type_change, type_member</li><li>[MagiskPolicy] Remove all AUDITDENY rules after patching sepolicy to log all denies for debugging</li><li>[MagiskBoot] Properly support extra_cmdline in boot headers</li><li>[MagiskBoot] Try to repair broken v1 boot image headers</li><li>[MagiskBoot] Add new CPIO command: &quot;exists&quot;</li></ul><h2 id="v17-3" tabindex="-1">v17.3 <a class="header-anchor" href="#v17-3" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Support boot image header v1 (Pixel 3)</li><li>[MagiskSU] No more linked lists for caching <code>su_info</code></li><li>[MagiskSU] Parse command-lines in client side and send only options to daemon</li><li>[MagiskSU] Early ACK to prevent client freezes and early denies</li><li>[Daemon] Prevent bootloops in situations where /data is mounted twice</li><li>[Daemon] Prevent logcat failures when /system/bin is magic mounting, could cause MagiskHide to fail</li><li>[Scripts] Switch hexpatch to remove Samsung Defex to a more general pattern</li><li>[Scripts] Update data encryption detection for better custom recovery support</li></ul><h2 id="v17-2" tabindex="-1">v17.2 <a class="header-anchor" href="#v17-2" aria-hidden="true">#</a></h2><ul><li>[ResetProp] Update to AOSP upstream to support serialized system properties</li><li>[MagiskInit] Randomize Magisk service names to prevent detection (e.g. FGO)</li><li>[MagiskSU] New communication scheme to communicate with Magisk Manager</li></ul><h2 id="v17-0-17-1" tabindex="-1">v17.0/17.1 <a class="header-anchor" href="#v17-0-17-1" aria-hidden="true">#</a></h2><ul><li>[General] Bring back install to inactive slot for OTAs on A/B devices</li><li>[Script] Remove system based root in addon.d</li><li>[Script] Add proper addon.d-v2 for preserving Magisk on custom ROMs on A/B devices</li><li>[Script] Enable KEEPVERITY when the device is using system_root_image</li><li>[Script] Add hexpatch to remove Samsung defex in new Oreo kernels</li><li>[Daemon] Support non ext4 filesystems for mirrors (system/vendor)</li><li>[MagiskSU] Make pts sockets always run in dev_pts secontext, providing all terminal emulator root shell the same power as adb shells</li><li>[MagiskHide] Kill all processes with same UID of the target to workaround OOS embryo optimization</li><li>[MagiskInit] Move all sepolicy patches pre-init to prevent Pixel 2 (XL) boot service breakdown</li></ul><h2 id="v16-7" tabindex="-1">v16.7 <a class="header-anchor" href="#v16-7" aria-hidden="true">#</a></h2><ul><li>[Scripts] Fix boot image patching errors on Android P (workaround the strengthened seccomp)</li><li>[MagiskHide] Support hardlink based ns proc mnt (old kernel support)</li><li>[Daemon] Fix permission of /dev/null after logcat commands, fix ADB on EMUI</li><li>[Daemon] Log fatal errors only on debug builds</li><li>[MagiskInit] Detect early mount partname from fstab in device tree</li></ul><h2 id="v16-6" tabindex="-1">v16.6 <a class="header-anchor" href="#v16-6" aria-hidden="true">#</a></h2><ul><li>[General] Add wrapper script to overcome weird <code>LD_XXX</code> flags set in apps</li><li>[General] Prevent bootloop when flashing Magisk after full wipe on FBE devices</li><li>[Scripts] Support patching DTB placed in extra sections in boot images (Samsung S9/S9+)</li><li>[Scripts] Add support for addon.d-v2 (untested)</li><li>[Scripts] Fix custom recovery console output in addon.d</li><li>[Scripts] Fallback to parsing sysfs for detecting block devices</li><li>[Daemon] Check whether a valid Magisk Manager is installed on boot, if not, install stub APK embedded in magiskinit</li><li>[Daemon] Check whether Magisk Manager is repackaged (hidden), and prevent malware from hijacking com.topjohnwu.magisk</li><li>[Daemon] Introduce new daemon: magisklogd, a dedicated daemon to handle all logcat related monitoring</li><li>[Daemon] Replace old invincible mode with handshake between magiskd and magisklogd, one will respawn the other if disconnected</li><li>[Daemon] Support GSI adbd bind mounting</li><li>[MagiskInit] Support detecting block names in upper case (Samsung)</li><li>[MagiskBoot] Check DTB headers to prevent false detections within kernel binary</li><li>[MagiskHide] Compare mount namespace with PPID to make sure the namespace is actually separated, fix root loss</li><li>[MagiskSU] Simplify <code>su_info</code> caching system, should use less resources and computing power</li><li>[MagiskSU] Reduce the amount of broadcasting to Magisk Manager</li><li>[ImgTool] Separate all ext4 image related operations to a new applet called &quot;imgtool&quot;</li><li>[ImgTool] Use precise free space calculation methods</li><li>[ImgTool] Use our own set of loop devices hidden along side with sbin tmpfs overlay. This not only eliminates another possible detection method, but also fixes apps that mount OBB files as loop devices (huge thanks to dev of Pzizz for reporting this issue)</li></ul><h2 id="v16-4" tabindex="-1">v16.4 <a class="header-anchor" href="#v16-4" aria-hidden="true">#</a></h2><ul><li>[Daemon] Directly check logcat command instead of detecting logd, should fix logging and MagiskHide on several Samsung devices</li><li>[Daemon] Fix startup Magisk Manager APK installation on Android P</li><li>[MagiskPolicy] Switch from AOSP u:r:su:s0 to u:r:magisk:s0 to prevent conflicts</li><li>[MagiskPolicy] Remove unnecessary sepolicy rules to reduce security penalty</li><li>[Daemon] Massive re-design /sbin tmpfs overlay and daemon start up</li><li>[MagiskInit] Remove <code>magiskinit_daemon</code>, the actual magisk daemon (magiskd) shall handle everything itself</li><li>[Daemon] Remove post-fs stage as it is very limited and also will not work on A/B devices; replaced with simple mount in post-fs-data, which will run ASAP even before the daemon is started</li><li>[General] Remove all 64-bit binaries as there is no point in using them; all binaries are now 32-bit only. Some weirdly implemented root apps might break (e.g. Tasker, already reported to the developer), but it is not my fault üòÉ</li><li>[resetprop] Add Protobuf encode/decode to support manipulating persist properties on Android P</li><li>[MagiskHide] Include app sub-services as hiding targets. This might significantly increase the amount of apps that could be properly hidden</li></ul><h2 id="v16-3" tabindex="-1">v16.3 <a class="header-anchor" href="#v16-3" aria-hidden="true">#</a></h2><ul><li>[General] Remove symlinks used for backwards compatibility</li><li>[MagiskBoot] Fix a small size calculation bug</li></ul><h2 id="v16-2" tabindex="-1">v16.2 <a class="header-anchor" href="#v16-2" aria-hidden="true">#</a></h2><ul><li>[General] Force use system binaries in handling ext4 images (fix module installation on Android P)</li><li>[MagiskHide] Change property state to disable if logd is disabled</li></ul><h2 id="v16-1" tabindex="-1">v16.1 <a class="header-anchor" href="#v16-1" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Fix MTK boot image packaging</li><li>[MagiskBoot] Add more Nook/Acclaim headers support</li><li>[MagiskBoot] Support unpacking DTB with empty kernel image</li><li>[MagiskBoot] Update high compression mode detection logic</li><li>[Daemon] Support new mke2fs tool on Android P</li><li>[resetprop] Support Android P new property context files</li><li>[MagiskPolicy] Add new rules for Android P</li></ul><h2 id="v16-0" tabindex="-1">v16.0 <a class="header-anchor" href="#v16-0" aria-hidden="true">#</a></h2><ul><li>[MagiskInit] Support non <code>skip_initramfs</code> devices with slot suffix (Huawei Treble)</li><li>[MagiskPolicy] Add rules for Magisk Manager</li><li>[Compiler] Workaround an NDK compiler bug that causes bootloops</li></ul><h2 id="v15-4" tabindex="-1">v15.4 <a class="header-anchor" href="#v15-4" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Support Samsung PXA, DHTB header images</li><li>[MagiskBoot] Support ASUS blob images</li><li>[MagiskBoot] Support Nook Green Loader images</li><li>[MagiskBoot] Support pure ramdisk images</li><li>[MagiskInit] Prevent OnePlus angela <code>sepolicy_debug</code> from loading</li><li>[MagiskInit] Obfuscate Magisk socket entry to prevent detection and security</li><li>[Daemon] Fix subfolders in /sbin shadowed by overlay</li><li>[Daemon] Obfuscate binary names to prevent naive detections</li><li>[Daemon] Check logd before force trying to start logcat in a loop</li></ul><h2 id="v15-3" tabindex="-1">v15.3 <a class="header-anchor" href="#v15-3" aria-hidden="true">#</a></h2><ul><li>[Daemon] Fix the bug that only one script would be executed in post-fs-data.d/service.d</li><li>[Daemon] Add <code>MS_SILENT</code> flag when mounting, should fix some devices that cannot mount magisk.img</li><li>[MagiskBoot] Fix potential segmentation fault when patching ramdisk, should fix some installation failures</li></ul><h2 id="v15-2" tabindex="-1">v15.2 <a class="header-anchor" href="#v15-2" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Fix dtb verity patches, should fix dm-verity bootloops on newer devices placing fstabs in dtb</li><li>[MagiskPolicy] Add new rules for proper Samsung support, should fix MagiskHide</li><li>[MagiskInit] Support non <code>skip_initramfs</code> devices using split sepolicies (e.g. Zenfone 4 Oreo)</li><li>[Daemon] Use specific logcat buffers, some devices does not support all log buffers</li><li>[scripts] Update scripts to double check whether boot slot is available, some devices set a boot slot without A/B partitions</li></ul><h2 id="v15-1" tabindex="-1">v15.1 <a class="header-anchor" href="#v15-1" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Fix faulty code in ramdisk patches which causes bootloops in some config and fstab format combos</li></ul><h2 id="v15-0" tabindex="-1">v15.0 <a class="header-anchor" href="#v15-0" aria-hidden="true">#</a></h2><ul><li>[Daemon] Fix the bug that Magisk cannot properly detect /data encryption state</li><li>[Daemon] Add merging <code>/cache/magisk.img</code> and <code>/data/adb/magisk_merge.img</code> support</li><li>[Daemon] Update to upstream libsepol to support cutting edge split policy custom ROM cil compilations</li></ul><h2 id="v14-6-1468" tabindex="-1">v14.6 (1468) <a class="header-anchor" href="#v14-6-1468" aria-hidden="true">#</a></h2><ul><li>[General] Move all files into a safe location: /data/adb</li><li>[Daemon] New invincible implementation: use <code>magiskinit_daemon</code> to monitor sockets</li><li>[Daemon] Rewrite logcat monitor to be more efficient</li><li>[Daemon] Fix a bug where logcat monitor may spawn infinite logcat processes</li><li>[MagiskSU] Update su to work the same as proper Linux implementation: Initialize window size; all environment variables will be migrated (except HOME, SHELL, USER, LOGNAME, these will be set accordingly), &quot;--preserve-environment&quot; option will preserve all variables, including those four exceptions. Check the Linux su manpage for more info</li><li>[MagiskBoot] Massive refactor, rewrite all cpio operations and CLI</li><li>[MagiskInit][magiskboot] Support ramdisk high compression mode</li></ul><h2 id="v14-5-1456" tabindex="-1">v14.5 (1456) <a class="header-anchor" href="#v14-5-1456" aria-hidden="true">#</a></h2><ul><li>[Magiskinit] Fix bootloop issues on several devices</li><li>[misc] Build binaries with NDK r10e, should get rid of the nasty linker warning when executing magisk</li></ul><h2 id="v14-5-1455" tabindex="-1">v14.5 (1455) <a class="header-anchor" href="#v14-5-1455" aria-hidden="true">#</a></h2><ul><li>[Daemon] Moved internal path to /sbin/.core, new image mountpoint is /sbin/.core/img</li><li>[MagiskSU] Support switching package name, used when Magisk Manager is hidden</li><li>[MagiskHide] Add temporary /magisk removal</li><li>[MagiskHide] All changes above contributes to hiding from nasty apps like FGO and several banking apps</li><li>[Magiskinit] Use magiskinit for all devices (dynamic initramfs)</li><li>[Magiskinit] Fix Xiaomi A1 support</li><li>[Magiskinit] Add Pixel 2 (XL) support</li><li>[Magiskboot] Add support to remove avb-verity in dtbo.img</li><li>[Magiskboot] Fix typo in handling MTK boot image headers</li><li>[script] Along with updates in Magisk Manager, add support to sign boot images (AVB 1.0)</li><li>[script] Add dtbo.img backup and restore support</li><li>[misc] Many small adjustments to properly support old platforms like Android 5.0</li></ul><h2 id="v14-3-1437" tabindex="-1">v14.3 (1437) <a class="header-anchor" href="#v14-3-1437" aria-hidden="true">#</a></h2><ul><li>[MagiskBoot] Fix Pixel C installation</li><li>[MagiskBoot] Handle special <code>lz4_legacy</code> format properly, should fix all LG devices</li><li>[Daemon] New universal logcat monitor is added, support plug-and-play to worker threads</li><li>[Daemon] Invincible mode: daemon will be restarted by init, everything should seamlessly through daemon restarts</li><li>[Daemon] Add new restorecon action, will go through and fix all Magisk files with selinux unlabeled to <code>system_file</code> context</li><li>[Daemon] Add brute-force image resizing mode, should prevent the notorious Samsung crappy resize2fs from affecting the result</li><li>[resetprop] Add new &quot;-p&quot; flag, used to toggle whether alter/access the actual persist storage for persist props</li></ul><h2 id="v14-2" tabindex="-1">v14.2 <a class="header-anchor" href="#v14-2" aria-hidden="true">#</a></h2><ul><li>[MagicMount] Clone attributes to tmpfs mountpoint, should fix massive module breakage</li></ul><h2 id="v14-1" tabindex="-1">v14.1 <a class="header-anchor" href="#v14-1" aria-hidden="true">#</a></h2><ul><li>[MagiskInit] Introduce a new init binary to support <code>skip_initramfs</code> devices (Pixel family)</li><li>[script] Fix typo in update-binary for x86 devices</li><li>[script] Fix stock boot image backup not moved to proper location</li><li>[script] Add functions to support A/B slot and <code>skip_initramfs</code> devices</li><li>[script] Detect Meizu boot blocks</li><li>[MagiskBoot] Add decompress zImage support</li><li>[MagiskBoot] Support extracting dtb appended to zImage block</li><li>[MagiskBoot] Support patching fstab within dtb</li><li>[Daemon/MagiskSU] Proper file based encryption support</li><li>[Daemon] Create core folders if not exist</li><li>[resetprop] Fix a bug which delete props won&#39;t remove persist props not in memory</li><li>[MagicMount] Remove usage of dummy folder, directly mount tmpfs and construct file structure skeleton in place</li></ul><h2 id="v14-0" tabindex="-1">v14.0 <a class="header-anchor" href="#v14-0" aria-hidden="true">#</a></h2><ul><li>[script] Simplify installation scripts</li><li>[script] Fix a bug causing backing up and restoring stock boot images failure</li><li>[script] Installation and uninstallation will migrate old or broken stock boot image backups to proper format</li><li>[script] Fix an issue with selabel setting in <code>util_functions.sh</code> on Lollipop</li><li>[rc script] Enable logd in post-fs to start logging as early as possible</li><li>[MagiskHide] magisk.img mounted is no longer a requirement Devices with issues mounting magisk.img can now run in proper core-only mode</li><li>[MagiskBoot] Add native function to extract stock SHA1 from ramdisk</li><li>[b64xz] New tool to extract compressed and encoded binary dumps in shell script</li><li>[busybox] Add busybox to Magisk source, and embed multi-arch busybox binary into update-binary shell script</li><li>[busybox] Busybox is added into PATH for all boot scripts (post-fs-data.d, service.d, and all module scripts)</li><li>[MagiskSU] Fully fix multiuser issues</li><li>[Magic Mount] Fix a typo in cloning attributes</li><li>[Daemon] Fix the daemon crashing when boot scripts opens a subshell</li><li>[Daemon] Adjustments to prevent stock Samsung kernel restrictions on exec system calls for binaries started from /data</li><li>[Daemon] Workaround on Samsung device with weird fork behaviors</li></ul><h2 id="v13-3" tabindex="-1">v13.3 <a class="header-anchor" href="#v13-3" aria-hidden="true">#</a></h2><ul><li>[MagiskHide] Update to bypass Google CTS (2017.7.17)</li><li>[resetprop] Properly support removing persist props</li><li>[uninstaller] Remove Magisk Manager and persist props</li></ul><h2 id="v13-2" tabindex="-1">v13.2 <a class="header-anchor" href="#v13-2" aria-hidden="true">#</a></h2><ul><li>[magiskpolicy] Fix magiskpolicy segfault on old Android versions, should fix tons of older devices that couldn&#39;t use v13.1</li><li>[MagiskHide] Set proper selinux context while re-linking /sbin to hide Magisk, should potentially fix many issues</li><li>[MagiskBoot] Change lzma compression encoder flag from <code>LZMA_CHECK_CRC64</code> to <code>LZMA_CHECK_CRC32</code>, kernel only supports latter</li><li>[General] Core-only mode now properly mounts systemless hosts and magiskhide</li></ul><h2 id="v13-1" tabindex="-1">v13.1 <a class="header-anchor" href="#v13-1" aria-hidden="true">#</a></h2><ul><li>[General] Merge MagiskSU, magiskhide, resetprop, magiskpolicy into one binary</li><li>[General] Add Android O support (tested on DP3)</li><li>[General] Dynamic link <a href="http://libselinux.so" target="_blank" rel="noreferrer">libselinux.so</a>, <a href="http://libsqlite.so" target="_blank" rel="noreferrer">libsqlite.so</a> from system to greatly reduce binary size</li><li>[General] Remove bundled busybox because it causes a lot of issues</li><li>[General] Unlock all block devices for read-write support instead of emmc only (just figured not all devices uses emmc lol)</li><li>[Scripts] Run all ext4 image operations through magisk binary in flash scripts</li><li>[Scripts] Updated scripts to use magisk native commands to increase compatibility</li><li>[Scripts] Add addon.d survival support</li><li>[Scripts] Introduce <code>util_functions.sh</code>, used as a global shell script function source for all kinds of installation</li><li>[MagiskBoot] Moved boot patch logic into magiskboot binary</li><li>[MagiskSU] Does not fork new process for each request, add new threads instead</li><li>[MagiskSU] Added multiuser support</li><li>[MagiskSU] Introduce new timeout queue mechanism, prevent performance hit with poorly written su apps</li><li>[MagiskSU] Multiple settings moved from prop detection to database</li><li>[MagiskSU] Add namespace mode option support</li><li>[MagiskSU] Add master-mount option</li><li>[resetprop] Updated to latest AOSP upstream, support props from 5.0 to Android O</li><li>[resetprop] Renamed all functions to prevent calling functions from external libc</li><li>[magiskpolicy] Updated libsepol from official SELinux repo</li><li>[magiskpolicy] Added xperm patching support (in order to make Android O work properly)</li><li>[magiskpolicy] Updated rules for Android O, and Liveboot support</li><li>[MagiskHide] Remove pseudo permissive mode, directly hide permissive status instead</li><li>[MagiskHide] Remove unreliable list file monitor, change to daemon request mode</li><li>[MagiskHide] MagiskHide is now enabled by default</li><li>[MagiskHide] Update unmount policies, passes CTS in SafetyNet!</li><li>[MagiskHide] Add more props for hiding</li><li>[MagiskHide] Remove background magiskhide daemon, spawn short life process for unmounting purpose</li><li>[Magic Mount] Ditched shell script based mounting, use proper C program to parse and mount files. Speed is SIGNIFICANTLY improved</li></ul><h2 id="v12-0" tabindex="-1">v12.0 <a class="header-anchor" href="#v12-0" aria-hidden="true">#</a></h2><ul><li>[General] Move most binaries into magisk.img (Samsung cannot run su daemon in /data)</li><li>[General] Move sepolicy live patch to <code>late_start</code> service This shall fix the long boot times, especially on Samsung devices</li><li>[General] Add Samsung RKP hexpatch back, should now work on Samsung stock kernels</li><li>[General] Fix installation with SuperSU</li><li>[MagiskHide] Support other logcat <code>am_proc_start</code> patterns</li><li>[MagiskHide] Change /sys/fs/selinux/enforce(policy) permissions if required Samsung devices cannot switch selinux states, if running on permissive custom kernel, the users will stuck at permissive If this scenario is detected, change permissions to hide the permissive state, leads to SafetyNet passes</li><li>[MagiskHide] Add built in prop rules to fake KNOX status Samsung apps requiring KNOX status to be 0x0 should now work (Samsung Pay not tested)</li><li>[MagiskHide] Remove all ro.build props, since they cause more issues than they benefit...</li><li>[MagiskBoot] Add lz4 legacy format support (most linux kernel using lz4 for compression is using this)</li><li>[MagiskBoot] Fix MTK kernels with MTK headers</li></ul><h2 id="v11-5-11-6" tabindex="-1">v11.5/11.6 <a class="header-anchor" href="#v11-5-11-6" aria-hidden="true">#</a></h2><ul><li>[Magic Mount] Fix mounting issues with devices that have separate /vendor partitions</li><li>[MagiskBoot] Whole new boot image patching tool, please check release note for more info</li><li>[magiskpolicy] Rename sepolicy-inject to magiskpolicy</li><li>[magiskpolicy] Update a rule to allow chcon everything properly</li><li>[MagiskHide] Prevent multirom crashes</li><li>[MagiskHide] Add patches for ro.debuggable, ro.secure, ro.build.type, ro.build.tags, ro.build.selinux</li><li>[MagiskHide] Change /sys/fs/selinux/enforce, /sys/fs/selinux/policy permissions for Samsung compatibility</li><li>[MagiskSU] Fix read-only partition mounting issues</li><li>[MagiskSU] Disable -cn option, the option will do nothing, preserved for compatibility</li></ul><h2 id="v11-1" tabindex="-1">v11.1 <a class="header-anchor" href="#v11-1" aria-hidden="true">#</a></h2><ul><li>[sepolicy-inject] Add missing messages</li><li>[magiskhide] Start MagiskHide with scripts</li></ul><h2 id="v11-0" tabindex="-1">v11.0 <a class="header-anchor" href="#v11-0" aria-hidden="true">#</a></h2><ul><li>[Magic Mount] Support replacing symlinks. Symlinks cannot be a target of a bind mounted, so they are treated the same as new files</li><li>[Magic Mount] Fix the issue when file/folder name contains spaces</li><li>[BusyBox] Updated to v1.26.2. Should fix the black screen issues of FlashFire</li><li>[resetprop] Support reading prop files that contains spaces in prop values</li><li>[MagiskSU] Adapt communication to Magisk Manager; stripped out unused data transfer</li><li>[MagiskSU] Implement SuperUser access option (Disable, APP only, ADB Only, APP &amp; ADB) phh Superuser app has this option but the feature isn&#39;t implemented within the su binary</li><li>[MagiskSU] Fixed all issues with su -c &quot;commands&quot; (run commands with root) This feature is supposed to only allow one single option, but apparently adb shell su -c &quot;command&quot; doesn&#39;t work this way, and plenty of root apps don&#39;t follow the rule. The su binary will now consider everything after -c as a part of the command.</li><li>[MagiskSU] Removed legacy context hack for TiBack, what it currently does is slowing down the invocation</li><li>[MagiskSU] Preserve the current working directory after invoking su Previously phh superuser will change the path to /data/data after obtaining root shell. It will now stay in the same directory where you called su</li><li>[MagiskSU] Daemon now also runs in u:r:su:s0 context</li><li>[MagiskSU] Removed an unnecessary fork, reduce running processes and speed up the invocation</li><li>[MagiskSU] Add -cn option to the binary Not sure if this is still relevant, and also not sure if implemented correctly, but hey it&#39;s here</li><li>[sepolicy-inject] Complete re-write the command-line options, now nearly matches supolicy syntax</li><li>[sepolicy-inject] Support all matching mode for nearly every action (makes pseudo enforced possible)</li><li>[sepolicy-inject] Fixed an ancient bug that allocated memory isn&#39;t reset</li><li>[uninstaller] Now works as a independent script that can be executed at boot Fully support recovery with no /data access, Magisk uninstallation with Magisk Manager</li><li>[Addition] Busybox, MagiskHide, hosts settings can now be applied instantly; no reboots required</li><li>[Addition] Add post-fs-data.d and service.d</li><li>[Addition] Add option to disable Magisk (MagiskSU will still be started)</li></ul><h2 id="v10-2" tabindex="-1">v10.2 <a class="header-anchor" href="#v10-2" aria-hidden="true">#</a></h2><ul><li>[Magic Mount] Remove apps/priv-app from whitelist, should fix all crashes</li><li>[phh] Fix binary out-of-date issue</li><li>[scripts] Fix root disappear issue when upgrading within Magisk Manager</li></ul><h2 id="v10" tabindex="-1">v10 <a class="header-anchor" href="#v10" aria-hidden="true">#</a></h2><ul><li>[Magic Mount] Use a new way to mount system (vendor) mirrors</li><li>[Magic Mount] Use universal way to deal with /vendor, handle both separate partition or not</li><li>[Magic Mount] Adding <strong>anything to any place</strong> is now officially supported (including /system root and /vendor root)</li><li>[Magic Mount] Use symlinks for mirroring back if possible, reduce bind mounts for adding files</li><li>[Magisk Hide] Check init namespace, zygote namespace to prevent Magic Mount breakage (a.k.a root loss)</li><li>[Magisk Hide] Send SIGSTOP to pause target process ASAP to prevent crashing if unmounting too late</li><li>[Magisk Hide] Hiding should work under any conditions, including adding libs and /system root etc.</li><li>[phh] Root the device if no proper root detected</li><li>[phh] Move <code>/sbin</code> to <code>/sbin_orig</code> and link back, fix Samsung no-suid issue</li><li>[scripts] Improve SuperSU integration, now uses sukernel to patch ramdisk, support SuperSU built in ramdisk restore</li><li>[template] Add PROPFILE option to load system.prop</li></ul><h2 id="v9" tabindex="-1">v9 <a class="header-anchor" href="#v9" aria-hidden="true">#</a></h2><ul><li><strong>[API Change] Remove the interface for post-fs modules</strong></li><li>[resetprop] New tool &quot;resetprop&quot; is added to Magisk to replace most post-fs modules&#39; functionality</li><li>[resetprop] Magisk will now patch &quot;ro.boot.verifiedbootstate&quot;, &quot;ro.boot.flash.locked&quot;, &quot;ro.boot.veritymode&quot; to bypass Safety Net</li><li>[Magic Mount] Move dummy skeleton / mirror / mountinfo filesystem tree to tmpfs</li><li>[Magic Mount] Rewritten dummy cloning mechanism from scratch, will result in minimal bind mounts, minimal file traversal, eliminate all possible issues that might happen in extreme cases</li><li>[Magic Mount] Adding new items to /system/bin, /system/vendor, /system/lib(64) is properly supported (devices with separate vendor partition is not supported yet)</li><li>[Magisk Hide] Rewritten from scratch, now run in daemon mode, proper list monitoring, proper mount detection, and maybe more.....</li><li>[Boot Image] Add support for Motorola boot image dtb, it shall now unpack correctly</li><li>[Uninstaller] Add removal of SuperSU custom patch script</li></ul><h2 id="v8" tabindex="-1">v8 <a class="header-anchor" href="#v8" aria-hidden="true">#</a></h2><ul><li>Add Magisk Hide to bypass SafetyNet</li><li>Improve SuperSU integration: no longer changes the SuperSU PATH</li><li>Support rc script entry points not located in init.rc</li></ul><h2 id="v7" tabindex="-1">v7 <a class="header-anchor" href="#v7" aria-hidden="true">#</a></h2><ul><li>Fully open source</li><li>Remove supolicy dependency, use my own sepolicy-injection</li><li>Run everything in its own selinux domain, should fix all selinux issues</li><li>Add Note 7 stock kernel hex patches</li><li>Add support to install Magisk in Magisk Manager</li><li>Add support for image merging for module flashing in Magisk Manager</li><li>Add root helpers for SuperSU auto module-ize and auto upgrading legacy phh superuser</li><li>New paths to toggle busybox, and support all root solutions</li><li>Remove root management API; both SuperSU and phh has their own superior solutions</li></ul><h2 id="v6" tabindex="-1">v6 <a class="header-anchor" href="#v6" aria-hidden="true">#</a></h2><ul><li>Fixed the algorithm for adding new files and dummy system</li><li>Updated the module template with a default permission, since people tend to forget them üòÉ</li></ul><h2 id="v5" tabindex="-1">v5 <a class="header-anchor" href="#v5" aria-hidden="true">#</a></h2><ul><li>Hotfix for older Android versions (detect policy before patching)</li><li>Update uninstaller to NOT uninstall Magisk Manager, since it cause problems</li></ul><h2 id="v4" tabindex="-1">v4 <a class="header-anchor" href="#v4" aria-hidden="true">#</a></h2><ul><li>Important: Uninstall v1 - v3 Magisk before upgrading with the uninstaller in the OP!!</li><li>Massive Rewrite Magisk Interface API! All previous mods are NOT compatible! Please download the latest version of the mods you use (root/xposed)</li><li>Mods are now installed independently in their own subfolder. This paves the way for future Magisk Manager versions to manage mods, <strong>just like how Xposed Modules are handled</strong></li><li>Support small boot partition devices (Huawei devices)</li><li>Use minimal sepolicy patch in boot image for smaller ramdisk size. Live patch policies after bootup</li><li>Include updated open source sepolicy injection tool (source code available), support nearly all SuperSU supolicy tool&#39;s functionality</li></ul><h2 id="v3" tabindex="-1">v3 <a class="header-anchor" href="#v3" aria-hidden="true">#</a></h2><ul><li>Fix bootimg-extract for Exynos Samsung devices (thanks to @phhusson), should fix all Samsung device issues</li><li>Add supolicy back to patch sepolicy (stock Samsung do not accept permissive domain)</li><li>Update sepolicy-injection to patch su domain for Samsung devices to use phh&#39;s root</li><li>Update root disable method, using more aggressive approach</li><li>Use lazy unmount to unmount root from system, should fix some issues with custom roms</li><li>Use the highest possible compression rate for ramdisk, hope to fix some devices with no boot partition space</li><li>Detect boot partition space insufficient, will abort installer instead of breaking your device</li></ul><h2 id="v2" tabindex="-1">v2 <a class="header-anchor" href="#v2" aria-hidden="true">#</a></h2><ul><li>Fix verity patch. It should now work on all devices (might fix some of the unable-to-boot issues)</li><li>All scripts will now run in selinux permissive mode for maximum compatibility (this will <strong>NOT</strong> turn your device to permissive)</li><li>Add Nougat Developer Preview 5 support</li><li>Add systemless host support for AdBlock Apps (enabled by default)</li><li>Add support for new root disable method</li><li>Remove sepolicy patches that uses SuperSU&#39;s supolicy tool; it is now using a minimal set of modifications</li><li>Removed Magisk Manager in Magisk patch, it is now included in Magisk phh&#39;s superuser only</li></ul><h2 id="v1" tabindex="-1">v1 <a class="header-anchor" href="#v1" aria-hidden="true">#</a></h2><ul><li>Initial release</li></ul><h2 id="ÂèÇËÄÉÈìæÊé•" tabindex="-1">ÂèÇËÄÉÈìæÊé• <a class="header-anchor" href="#ÂèÇËÄÉÈìæÊé•" aria-hidden="true">#</a></h2><ul><li><a href="https://topjohnwu.github.io/Magisk/changes.html" target="_blank" rel="noreferrer">Magisk Magisk Changelog</a></li></ul>',137),s=[t];function n(r,d,c,p,u,h){return l(),e("div",null,s)}const v=i(a,[["render",n]]);export{m as __pageData,v as default};
